<?php
App::uses('AppModel', 'Model');
class Template extends AppModel {
	
	public $name = 'Template';
		
/**
 * Before save callback
 * 
 * @todo check template for well formatted content
 * @todo template name should be unique (between here, and the webpages table)
 * @todo css and js files must have the full file name (with .css and .js on the end)
 * @todo all element names, css names, and js names should be pre-fixed with the template name (eg. sidebar-footer, sidebar-custom.css)
 * @todo all {page: anything} should be wrapped in a div of some kind (should not be <div>{page: something} <div> more html </div></div>)
 * @todo bonus points for running the html through a validator :)
 * 
	public function beforeSave($options = array()) {
		return parent::beforeSave($options);
	}
 */
	
/**
 * After find callback
 * 
 * @param array
 * @param boolean
 * @return array
 */
	public function afterFind($results = array(), $primary = null) {
		for ($i = 0; $i < count($results); $i++) {
			// the 'put the icon html here' part of that if statement is temporary and can be removed soon 9/6/2013 RK
			$results[$i][$this->alias]['icon'] = !empty($results[$i][$this->alias]['icon']) && $results[$i][$this->alias]['icon'] != 'put the icon html here' ? $results[$i][$this->alias]['icon'] : $this->dummyfield('icon');
		}
		return parent::afterFind($results, $primary);
	}

/**
 * Placeholder method
 * 
 */
 	public function placeholder() {
 		$data[0][$this->alias]['icon'] = $this->dummyfield('icon');
 		$data[0][$this->alias]['layout'] = $this->dummyfield('layout');
 		return $data;
 	}

/**
 * dummyField method 
 * 
 */
 	public function dummyfield($field) {
 		switch ($field) {
			case 'icon':
				return '<div style="height: 0px; padding-bottom: 80%; position:relative; width: 100%; float: left;"><div style="width: 100%; height: 100%; padding: 0; top: 0; position: absolute; background: url(data:image/gif;base64,) no-repeat;  background-size: cover; border: 1px solid #E3E3E3; border-radius: 1em; overflow: hidden;"></div></div>';
				break;
			case 'layout':
				return 'no-template.ctp';
				break;
			default:
				return null;				 
				break;
		}
 	}

}