// replace tag snippet
(function($){
  $.fn.replaceTagName = function(a){
    var t = [];
    for(var i = this.length - 1; 0 <= i ; i--) {
      var n = document.createElement(a);
      n.innerHTML = this[i].innerHTML;
      $.each(this[i].attributes, function(j, v) {
        $(n).attr(v.name, v.value);
      });
      $(this[i]).after(n).remove();
      t[i] = n;
    }
    return $(t);
  };
})(jQuery);


$(function() {
    // Default tooltip selector 
    $("a[rel=tooltip]").tooltip();
    // tables
	$('.container table:not(#webpage_content table)').addClass('table');
	
    //handle money inputs (which are denoted by two decimal places)
    $('input[step="0.01"]').width(function() {
        $(this).width($(this).width() - 28);
        $(this).before('<span class="add-on">$</span>');
        $(this).parent().addClass('input-prepend');
    });
    
	$(".indexRow").each(function(index) {
		$(".metaCell", this).detach().appendTo($(".indexData", this));
		$(".metaCell", this).addClass("nav-collapse");
		$(".metaCell", this).addClass("nav-collapse" + index);
        // if you comment something out, tell us why!!, so we can check to see if we broke something bringing it back!!!)
		//$('.indexData', this).prepend("<a class=\"btn btn-navbar pull-right\" data-toggle=\"collapse\" data-target=\".nav-collapse" + index + "\"><span class=\"icon-bar\"></span><span class=\"icon-bar\"></span><span class=\"icon-bar\"></span></a>");
        
        $(".actionCell", this).addClass("nav-collapse").addClass("nav-collapse" + index);
	}).after('<hr />').addClass('row-fluid');
    
	$(".actionCell ul").addClass("nav nav-pills");
	$(".titleCell").addClass("brand");
	$(".indexCell").each(function(index) {
		titleCell = $(".actionCell", this).detach();
		titleCell.insertAfter($(".titleCell", this));
	});
	
	$(".imageCell").each(function(index) {
		if (!$("img", this).length) {
			$(this).hide();
		}
	});
	// context menu
	$(".btn-group a").addClass("btn");
	$(".btn-group a.delete").addClass("btn-danger");
	$(".btn-group a.add").addClass("btn-success");
	$(".btn-group a.add, .contacts.view .estimate.btn, .contacts.view .activity.btn, .contacts.view .task.btn").prepend("<i class=\"icon-plus\"></i>");
	$(".btn-group a.add, .contacts.view .estimate.btn, .contacts.view .activity.btn, .contacts.view .task.btn").addClass("btn-success");
	$(".btn-group a.dashboard").prepend("<i class=\"icon-leaf icon-white\"></i>");
	$(".btn-group a.dashboard").addClass("btn-primary");	
	
	
	// pagination 
	$(".paging").addClass("pagination");
	$(".paging span:first-child").addClass("previous");
	$(".paging span:last-child").addClass("next");
	$(".paging span").wrapAll("<ul />");
	$(".paging ul span").wrap("<li />");
	
	// messages
	$("#flashMessage, #authMessage").addClass("alert alert-info").prepend("<button type=\"button\" class=\"close\" data-dismiss=\"alert\">×</button>");
    
    //toggle (removed because its showing a caret on the narrower screens, like /projects/index)
    // need to make it work only in the places we want it before bringing it back
    //$(".indexContainer *[data-toggle=collapse]").append('<b class="caret"></b>');
    //$(".collapsed[data-toggle=collapse] .caret").replaceWith('<b class="right-caret"></b>');
    //$("*[data-toggle=collapse]").live('click', function() {
    //	if ($(".right-caret", this).length > 0) {
    //		$(".right-caret", this).replaceWith('<b class="caret"></b>');
    //	} else {
    //		$(".caret", this).replaceWith('<b class="right-caret"></b>');
    //	}
    //});
    $("*[data-toggle=collapse]").css('cursor', 'pointer');
    
    // sidebar with sub ul's
    $("body#sections_index").attr("data-spy", "scroll"); // move this to that page
    $("body#sections_index").attr("data-target", ".bs-docs-sidebar"); // move this to that page
//    $(window).scroll(function() {
//        $("ul li .active").parent().parent().addClass("active");
//    });
    $(".bs-docs-sidenav li ul").hide();
    $(".bs-docs-sidenav li a").click(function () {
        $me = $(this);
        $($me).next().toggle('slow', function() {
            if ($("i", $me).hasClass("icon-chevron-right")) {
                $("i", $me).removeClass("icon-chevron-right");
                $("i", $me).addClass("icon-chevron-down");
            } else {
                $("i", $me).removeClass("icon-chevron-down");
                $("i", $me).addClass("icon-chevron-right");
            }
        });
    });
    
    /* forms */
    $('input[type=submit]').addClass('btn');
    $('.required').attr('required', true);
    $('legend.toggleClick, legend.sectionTitle').wrapInner('<small />');
    $('#GalleryImageFilename').parent().addClass('fileupload');
    $('#GalleryImageFilename').parent().addClass('fileupload-new');
    $('#GalleryImageFilename').parent().attr('data-provides', 'fileupload');
    $('#GalleryImageFilename').before(' <div class="fileupload-new thumbnail"> <i class="icon-edit"></i> </div> <div class="fileupload-preview fileupload-exists thumbnail"></div> ');
    $('#GalleryImageFilename').after(' <a href="#" class="btn fileupload-exists" data-dismiss="fileupload">Remove</a> ');
    $('#GalleryImageFilename').wrap('<span class="btn btn-file" />');
    $('#GalleryImageFilename').before(' <span class="fileupload-new">Select image</span> <span class="fileupload-exists">Change</span> ');
    // tight responsive table for a form
    $('fieldset.control-group', $('.form-responsive ')).wrapInner('<tbody />').wrapInner('<table class="table table-hover" />');
    $('fieldset.control-group div.input', $('.form-responsive ')).replaceTagName('tr');
    $('fieldset.control-group legend, fieldset tr.input label, fieldset tr.input input[type!="hidden"], fieldset tr.input select, fieldset tr.input textarea', $('.form-responsive')).wrap('<td />');
    var max = 0;
    var trs = $('fieldset.control-group tr', $('.form-responsive'));
    trs.each(function() {
        if ($(this).children('td').length > max) {
            max = $(this).children('td').length;
        }
    });
    trs.each(function() {
        var kids = $(this).children('td').length;
        if (kids < max) {
            $('td:last-child', $(this)).attr('colspan', kids - 1);
        }
        
        if (kids == 2) {
            $('td:last-child', $(this)).attr('colspan', max - 1);
            $('td input[type!="checkbox"], td select, td texarea', $(this)).css('width', '92.5%'); 
        } else {
            $('td input, td select, td texarea', $(this)).css('width', '72%');
        }
    });
    $('td legend', $('.form-responsive')).parent().attr('colspan', max).wrap('<tr />');
    $('.form-responsive legend').parent().hover().css('background', 'white');
    
	
	
/**
 * Hides form elements that come after a legend with the class toggleClick
 * <legend class="toggleClick">Legend Text</legend>
 */
  	$('legend.toggleClick').siblings().hide();
	$('legend.toggleClick').addClass("toggle");
	
  	$('body').on("click", "legend.toggleClick", function(){
    	$(this).siblings().slideToggle("toggle");
		if ($(this).is(".toggle")) {
			$(this).removeClass("toggle");
			$(this).addClass("toggled");
		} else {
			$(this).removeClass("toggled");
			$(this).addClass("toggle");
		}
    });
	
/**
 * site wide toggle, set the click elements class to toggleClick, 
 * and the data-target attribute to the selector of the element you want to toggle. 
 * example:  <div class="toggleClick" data-target="#someId + .someClass">click to toggle</div>
 */
	$(".toggleClick, .toggleHover, .showClick").css('cursor', 'pointer');
	$(".toggleClick[data-target], .toggleHover[data-target], .showClick[data-target]").each( function(index) {
		var currentName = $(this).attr("data-target");
		$(currentName).hide();
        $(this).removeClass('toggled');
        $(this).addClass('toggle');
	});
	
	$(".toggleClick[data-target]").click(function (e) {
		var currentName = $(this).attr('data-target');
		$(currentName).toggle();
        $(this).removeClass('toggle');
        $(this).addClass('toggled');
		return false;
	});
	
	$(".toggleHover[data-target]").hover(function () {
		var currentName = $(this).attr('data-target');
		$(currentName).toggle();
		return false;
	});
	
	$(".showClick[data-target]").click(function () {
		var currentName = $(this).attr('data-target');
		$(currentName).show('slow');
		return false;
	});
	

    $('.checkboxToggleDiv').each(function () {
        var c = $(this).children('input[type=checkbox]').first();
        c.wrap('<label class="toggle well header-toggle" style="width:100px;" />');
        c.after('<p><span><i>On</i></span><span><i>off</i></span></p><a class="btn btn-mini slide-button"></a>');
        c.change(function(e){
        	toggleMode($(e.target));
        });
    	
    	toggleMode(c);
    	
    });
    
    $('.checkboxToggle').each(function () {
        $(this).wrap('<label class="toggle well header-toggle" style="width:100px;" />');
        $(this).after('<p><span><i>On</i></span><span><i>off</i></span></p><a class="btn btn-mini slide-button"></a>');
    });
	
});

function toggleMode(obj) {
		var p = obj.closest('div.checkboxToggleDiv');
        if (obj.is(':checked')) {
            //if there is both on and off states
            if(p.find('.ct-off').length > 0) {
            	 p.find('.ct-off').fadeOut('slow', function() {
                	p.find('.ct-on').fadeIn('slow');
            	});
            }else {
            	p.find('.ct-on').fadeIn('slow');
            }
            
           
        } else {
            if(obj.find('.ct-off').length > 0) {
	            p.find('.ct-on').fadeOut('slow', function() {
	                p.find('.ct-off').fadeIn('slow');
	            });
        	}else {
            	p.find('.ct-on').fadeOut('slow');
            }
        }
}
